var investAmountWrapper=document.querySelector(".invest-amount-wrapper"),investAmountInput=document.querySelector(".invest-amount");function focusClassToggle(){investAmountWrapper.classList.toggle("invest-amount-wrapper--focus")}function numberWithCommas(x){return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g,",")}investAmountInput.addEventListener("focus",focusClassToggle),investAmountInput.addEventListener("blur",focusClassToggle);